
public class Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		test();

	}

	private static void test() {
		// TODO Auto-generated method stub
		int number=2;
		switch(number)
		{
		case 1:System.out.println("number is one:" +number);
		break;
		case 2 :System.out.println("number is two:" + number);
		break;
		default:System.out.println("default");
		}
		
	}

}
